// script.js
require('dotenv').config();
const mongoose = require('mongoose');
const { Event } = require('./db'); // adjust path if needed

async function run() {
  try {
    await mongoose.connect(process.env.MONGO_URI, {
      useNewUrlParser: true,
      useUnifiedTopology: true,
    });

    const events = await Event.find().lean();

    console.log('All events with createdBy:');
    events.forEach((e) => {
      console.log(`- ${e.date} | ${e.name} | createdBy: ${e.createdBy}`);
    });

    mongoose.disconnect();
  } catch (err) {
    console.error('Error running script:', err);
    process.exit(1);
  }
}

run();
