+require('dotenv').config();
+const express = require('express');
+const { connectDB, Event } = require('./db');
+const authRoutes = require('./routes/auth');
+const cors = require('cors');
+const authMiddleware = require('./middleware/middleware'); 
+
+const app = express();
+
+connectDB();
+
+app.use(cors({
+  origin: 'http://localhost:3000',
+  credentials: true
+}));
+
+app.use(express.json());
+app.use('/api/admin', authRoutes);
+
+// Use MongoDB for events instead of in-memory array
+
+// Secure these routes
+app.get('/api/events', authMiddleware, async (req, res) => {
+  try {
+    const events = await Event.find({ status: { $ne: 'CANCELLED' } });
+    res.json(events);
+  } catch (err) {
+    res.status(500).json({ message: 'Error fetching events' });
+  }
+});
+
+app.post('/api/events', authMiddleware, async (req, res) => {
+  try {
+    const newEvent = new Event({
+      ...req.body,
+      createdBy: req.admin?.email || 'unknown',
+    });
+    const savedEvent = await newEvent.save();
+    res.status(201).json(savedEvent);
+  } catch (err) {
+    res.status(500).json({ message: 'Error creating event' });
+  }
+});
+
+app.put('/api/events/:id', authMiddleware, async (req, res) => {
+  try {
+    const updatedEvent = await Event.findByIdAndUpdate(req.params.id, req.body, { new: true });
+    if (!updatedEvent) return res.status(404).json({ message: 'Event not found' });
+    res.json(updatedEvent);
+  } catch (err) {
+    res.status(500).json({ message: 'Error updating event' });
+  }
+});
+
+app.delete('/api/events/:id', middlewaree, async (req, res) => {
+  try {
+    await Event.findByIdAndUpdate(req.params.id, { status: 'CANCELLED' });
+    res.status(204).end();
+  } catch (err) {
+    res.status(500).json({ message: 'Error deleting event' });
+  }
+});
+
+const PORT = process.env.PORT || 5000;
+app.listen(PORT, () => {
+  console.log(`Server running on port ${PORT}`);
+});

