+const express = require('express');
+const { connectDB, Event } = require('./db');
+const authRoutes = require('./routes/auth');
+const cors = require('cors');
+const middlewaree = require('./middleware/middleware'); 
+
+const app = express();
+
+connectDB();
+
+app.use(cors({
+  origin: 'http://localhost:3000',
+  credentials: true
+}));
+
+app.use(express.json());
+app.use('/api/admin', authRoutes);
+
+
+
+app.use('/api/events', authMiddleware);
+
+//  GET all events by admin (not cancelled)
+app.get('/api/events', async (req, res) => {
+  try {
+    const events = await Event.find({
+      createdBy: req.admin._id,
+      status: { $ne: 'CANCELLED' }
+    });
+    res.json(events);
+  } catch (err) {
+    console.error(err);
+    res.status(500).json({ message: 'Error fetching events' });
+  }
+});
+
+//  POST create event with createdBy
+app.post('/api/events', async (req, res) => {
+  try {
+    const newEvent = new Event({
+      ...req.body,
+      createdBy: req.admin._id
+    });
+    const savedEvent = await newEvent.save();
+    res.status(201).json(savedEvent);
+  } catch (err) {
+    console.error(err);
+    res.status(500).json({ message: 'Error creating event' });
+  }
+});
+
+//  PUT update event only if belongs to this admin
+app.put('/api/events/:id', async (req, res) => {
+  try {
+    const event = await Event.findOneAndUpdate(
+      { _id: req.params.id, createdBy: req.admin._id },
+      req.body,
+      { new: true }
+    );
+    if (!event) return res.status(404).json({ message: 'Event not found or unauthorized' });
+    res.json(event);
+  } catch (err) {
+    console.error(err);
+    res.status(500).json({ message: 'Error updating event' });
+  }
+});
+
+//  DELETE (soft delete) event
+app.delete('/api/events/:id', async (req, res) => {
+  try {
+    const event = await Event.findOneAndUpdate(
+      { _id: req.params.id, createdBy: req.admin._id },
+      { status: 'CANCELLED' }
+    );
+    if (!event) return res.status(404).json({ message: 'Event not found or unauthorized' });
+    res.status(204).end();
+  } catch (err) {
+    console.error(err);
+    res.status(500).json({ message: 'Error deleting event' });
+  }
+});
+
+
+
+
+
+const PORT = process.env.PORT || 5000;
+app.listen(PORT, () => {
+  console.log(`Server running on port ${PORT}`);
+});

